import axios from 'axios';
import { APIBase } from '../Utilities/Constants'
import { FETCHING_COIN_DATA, FETCHING_COIN_DATA_SUCCESS, FETCHING_COIN_DATA_FAIL} from '../Utilities/ActionTypes'

export default function FetchCoinData() {


    return dispatch => {

        dispatch({ type: FETCHING_COIN_DATA })

        return fetch(`${APIBase}/v1/cryptocurrency/listings/latest`, {
            method: 'GET',
            headers: {
            'X-CMC_PRO_API_KEY': 'c9d10fc5-5a77-4b12-9968-e67a369badd7'
        }
    })
            .then(response => response.json()).then(json => { 
                this.setState({
                    isFetching: false,
                    hasError: false,
                    data: json,
                    errorMessage: null
                }


            //         {
