    return dispatch => {

        dispatch({ type: FETCHING_COIN_DATA })

        return fetch('https://sandbox-api.coinmarketcap.com/v1/cryptocurrency/listings/latest?start=1&limit=5000&convert=USD', {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'X-CMC_PRO_API_KEY': 'c9d10fc5-5a77-4b12-9968-e67a369badd7'
            },
            json: true,
            gzip: true
    })
            .then(response => {
                dispatch({ type: FETCHING_COIN_DATA_SUCCESS, payload: response.data })
            }).then(console.log(this.state))
